function gulpFoxie(){
	this.styles = function(gulp, map, stylus, livereload){
		gulp.src(map.styles.main)
				.pipe(stylus())
				.pipe(gulp.dest(map.styles.dest))
				.pipe(livereload());
	}
	this.scripts = function(gulp, map, browserify, uglify, livereload){
		gulp.src(map.scripts.recursive.js)
			.pipe(browserify())
			.pipe(uglify())
			.pipe(gulp.dest(map.scripts.dest))
			.pipe(livereload())
	}
	this.json = function(gulp, map, livereload){

		gulp.src(map.json.src.styl.assign)
			.pipe(gulp.dest(map.json.dest.js.assign))
			.pipe(livereload());
	}
}

gulpFoxie = new gulpFoxie()
module.exports = gulpFoxie;