//Foxie framework uses json to assign classes, these classes can be changed, during this change a duplicate will be piped to the js folder allowing you to access the component classes without needing to remember the names of the classes. all vars are stored in the ff Object rather than in the window object. The selectors are preformatted in a Sizzle/jquery/domtastic/zepto fashion: $(". ") to see what is available to use just console.log(ff).

var ffAssign = require('./settings/foxie-assign.json')

var ff = {}

var component = {
	json:ffAssign,
	keys:Object.keys(ffAssign),
	get length(){return this.keys.length}
}

//console.log(component.json)

for(i = 0; i < component.length; i++){
	//components
	var componentNames = component.keys[i]
	//console.log(componentNames)

	//modifier
	var modifer = Object.keys(component.json[componentNames])
//	console.log(modifer)

	//modifier length
	var modiferLength = modifer.length;

	for(x = 0; x < modiferLength; x++){
		//define variable or object? bellow is the key names

		var varName = componentNames+"-"+modifer[x];

		var varName = varName.replace(/-([a-z0-9])/g, function (g) { return g[1].toUpperCase(); });

		var classes = component.json[componentNames][modifer[x]]

		var selector = classes
		ff[varName] = selector;

	}

}

module.exports = ff;