var $ = require('jquery-browserify')

var utils = {

	switchClass: function switchClass(el, classes) {
		el.toggleClass(classes[0].split('.').join(""))
		el.toggleClass(classes[1].split('.').join(""))
	},

	switchAttr: function switchAttr(el, dataName, transform) {
		var data = "data-";
		console.log()
		if (el.attr(data + dataName, transform.from)) {
			el.attr(data + dataName, transform.to);
		} else {
			console.log("Looked at " + el[0].nodeName + "." + el[0].classList[0] + " but could not find attr: " + data + dataName)
		}
	},

	toggleFunction: function(bool, stateOne, stateTwo){
		if(!bool){
			stateOne();
		}
		else{
			stateTwo();
		}
	}
}

module.exports = utils;