var path = require('path'),
		r = path.normalize(path.resolve(__dirname,"../")),
		src = path.normalize(r+"/src/"),
		dist = path.normalize(r+"/dist/")

var map = {
	"module":{
		root:r,
		package:path.normalize(r+"/package.json")
	},
	"scripts":{
		"src":path.normalize(src+"/js/"),
		"dest":path.normalize(dist+"/js/"),
		"recursive":{
			"js":path.normalize(src+"/js/**/*.js"),
			"json":path.normalize(src+"/js/**/*.json")
		}
	},
	"styles":{
		"main":path.normalize(src+"/styl/index.styl"),
		"src":path.normalize(src+"/styl/"),
		"dest":path.normalize(dist+"/css/"),
		"output":path.normalize(dist+"/css/bundle.css"),
		"recursive":{
			"styl":path.normalize(src+"/styl/**/*.styl"),
			"json":path.normalize(src+"/styl/**/*.json")
		}
	},
	sync:{
		src:{
			settings:path.normalize(src+"styl/settings")
		},
		dest:{
			settings:path.normalize(src+"js/settings")
		}
	},
	"gulpTasks":{
		"scripts":path.normalize(src+"/gulp-tasks/foxie-scripts.js"),
		"styles":path.normalize(src+"/gulp-tasks/foxie-styles.js"),
		"sync":path.normalize(src+"/gulp-tasks/foxie-sync.js"),
		"icons":path.normalize(src+"/gulp-tasks/foxie-icons.js")
	}
}

module.exports = map;